/* CaffeeCo. is a merchant who is selling on an e-commerce marketplace. 
  There the very basic data the company have  as follows */

-- Starting with creating database

Create database my_database_project;
Use my_database_project;

-- Let's start to create our tables with constraints

CREATE TABLE country (
    country_id SMALLINT PRIMARY KEY,
    Country VARCHAR(255)
);

CREATE TABLE Customers (
    id INT AUTO_INCREMENT NOT NULL,
    first_name VARCHAR(55) NOT NULL,
    Last_name VARCHAR(55) NOT NULL,
    Phone_no VARCHAR(25),
    email VARCHAR(55),
    gender ENUM('f', 'm', 'Prefer not to say'),
    type MEDIUMINT,
    country_id SMALLINT,
    birth_date DATE,
    sms_permission TINYINT,
    push_permission TINYINT,
    email_permission TINYINT,
    Date_enrolled DATE,
    PRIMARY KEY (id),
    FOREIGN KEY (country_id)
        REFERENCES country (country_id)
        ON DELETE SET NULL
);

 
CREATE TABLE customer_segmentation (
    customer_type MEDIUMINT,
    Type_expression VARCHAR(55),
    PRIMARY KEY (customer_type)
);

-- Let's change a datatype

Alter table customers
Add foreign key (type) references customer_segmentation(customer_type) on delete cascade;

CREATE TABLE order_status (
    id SMALLINT NOT NULL PRIMARY KEY,
    name VARCHAR(255)
);

CREATE TABLE orders (
    id INT(8) AUTO_INCREMENT PRIMARY KEY,
    status SMALLINT,
    Customer INT,
    Date_add DATETIME NOT NULL,
    ship_country_code VARCHAR(55),
    bill_country_code VARCHAR(55),
    order_total_price FLOAT,
    order_sold_price FLOAT,
    order_discount FLOAT,
    coupon_code INT,
    gross_profit FLOAT,
    Logistic_partner VARCHAR(55),
    cargo_partner VARCHAR(55),
    date_delivered DATETIME,
    FOREIGN KEY (customer)
        REFERENCES customers (id)
        ON DELETE CASCADE,
    FOREIGN KEY (Status)
        REFERENCES order_status (id)
        ON DELETE SET NULL
);

